import { navigate } from '@/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
const databaseModel = {
  dbHost: '',
  dbPort: '',
  dbName: '',
  dbUser: '',
  enableDataEncryption: false
};
export default {
  name: 'ConfirmationScreen',
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/installer/api/database-config');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      database: {
        ...databaseModel
      },
      adminUserName: ''
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.getAll().then(response => {
      const {
        data
      } = response.data;
      this.database = {
        ...databaseModel,
        ...data
      };
      return this.http.request({
        method: 'GET',
        url: '/installer/api/admin-user'
      });
    }).then(response => {
      const {
        data
      } = response.data;
      this.adminUserName = data.username;
      this.isLoading = false;
    });
  },
  methods: {
    onSubmit() {
      navigate('/installer/process');
    },
    navigateUrl() {
      navigate('/installer/admin-user-creation');
    }
  }
};
