import { APIService } from '@/core/util/services/api.service';
import FlexTable from '@/components/FlexTable.vue';
import { navigate } from '@/core/util/helper/navigation.ts';
export default {
  name: 'SystemCheckScreen',
  components: {
    'flex-table': FlexTable
  },
  props: {
    installer: {
      type: Boolean,
      default: false
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `${props.installer ? 'installer' : 'upgrader'}/api/system-check`);
    return {
      http
    };
  },
  data() {
    return {
      items: [],
      isLoading: false,
      isInterrupted: true
    };
  },
  beforeMount() {
    this.fetchData();
  },
  methods: {
    fetchData() {
      this.isLoading = true;
      this.http.getAll().then(response => {
        const {
          data,
          meta
        } = response.data;
        this.items = data;
        this.isInterrupted = meta.isInterrupted;
      }).finally(() => {
        this.isLoading = false;
      });
    },
    navigateUrl() {
      navigate(this.installer ? '/installer/database-config' : '/upgrader/database-config');
    },
    goToScreen() {
      navigate(this.installer ? '/installer/instance-creation' : '/upgrader/current-version');
    },
    reCheck() {
      this.fetchData();
    }
  }
};
